namespace ECC {

	void SetRandom(uintBig& x)
	{
		GenRandom(x);
	}

	void SetRandom(Scalar::Native& x)
	{
		Scalar s;
		while (true)
		{
			SetRandom(s.m_Value);
			if (!x.Import(s))
				break;
		}
	}

	void SetRandom(Key::IKdf::Ptr& pRes)
	{
		uintBig seed;
		SetRandom(seed);
		HKdf::Create(pRes, seed);
	}

}

int g_TestsFailed = 0;

void TestFailed(const char* szExpr, uint32_t nLine)
{
	printf("Test failed! Line=%u, Expression: %s\n", nLine, szExpr);
	g_TestsFailed++;
	fflush(stdout);
}

#define verify_test(x) \
	do { \
		if (!(x)) \
			TestFailed(#x, __LINE__); \
	} while (false)

#define fail_test(msg) TestFailed(msg, __LINE__)

namespace beam
{

	struct MyProcessor
		:public EvmProcessor
	{
		struct ContractData
			:public intrusive::set_base_hook<Address>
		{
			ByteBuffer m_Code;
			std::map<Word, Word> m_Vars;
		};

		intrusive::multiset_autoclear<ContractData> m_mapContracts;

		ContractData* m_pCurrent = nullptr;

		ContractData& get_Current()
		{
			assert(!m_lstFrames.empty());
			auto& f = m_lstFrames.back();
			
			if (!m_pCurrent || (m_pCurrent->m_Key != f.m_Address))
			{
				auto it = m_mapContracts.find(f.m_Address, ContractData::Comparator());
				assert(m_mapContracts.end() != it);
				m_pCurrent = &(*it);
			}

			return *m_pCurrent;
		}

		void SStore(const Word& key, const Word& w) override
		{
			auto& vars = get_Current().m_Vars;
			if (w == Zero)
			{
				auto it = vars.find(key);
				if (vars.end() != it)
					vars.erase(it);
			}
			else
				vars[key] = w;
		}

		bool SLoad(const Word& key, Word& w) override
		{
			auto& vars = get_Current().m_Vars;
			auto it = vars.find(key);
			if (vars.end() == it)
				return false;

			w = it->second;
			return true;
		}

		void RunFull(const Address& aCaller, const Blob& args)
		{
			Reset();
			assert(m_pCurrent);
			assert(m_lstFrames.empty());

			auto& f = PushFrame(m_pCurrent->m_Key);
			f.m_Args.m_Buf = args;
			f.m_Gas = 1000000000ULL;

			if (!m_pCurrent->m_Code.empty())
			{
				f.m_Code.m_p = &m_pCurrent->m_Code.front();
				f.m_Code.m_n = (uint32_t) m_pCurrent->m_Code.size();
			}

			while (!m_lstFrames.empty())
				RunOnce();
		}

		bool Construct(const Address& aContract, const Address& aCaller, const char* szCode, uint32_t nLenCode, const void* pArg, uint32_t nArg)
		{
			m_pCurrent = m_mapContracts.Create(aContract);
			auto& c = *m_pCurrent;

			uint32_t nSizeCode = nLenCode / 2;
			c.m_Code.resize(nSizeCode + nArg);
			if (nSizeCode)
			{
				auto ret = uintBigImpl::_Scan(&c.m_Code.front(), szCode, nLenCode);
				verify_test(ret == nLenCode);
				memcpy(&c.m_Code.front() + nSizeCode, pArg, nArg);
			}

			std::cout << "\nCtor" << std::endl;

			RunFull(aCaller, Blob());
			if (m_RetVal.m_Success)
				m_RetVal.m_Blob.Export(c.m_Code); // save retval as the remaining code, rerun
			else
				m_mapContracts.Delete(c);

			Reset();

			return m_RetVal.m_Success;
		}

		bool RunMethod(const Address& aContract, const Address& aCaller, const EvmProcessor::Method& m, uint32_t nSizeMethod)
		{
			std::cout << "\nCalling method " << m.m_Selector << std::endl;

			auto it = m_mapContracts.find(aContract, ContractData::Comparator());
			if (m_mapContracts.end() == it)
				return false;

			m_pCurrent = &(*it);

			RunFull(aCaller, Blob(&m, nSizeMethod));
			return m_RetVal.m_Success;

		}

		template <typename TMethod>
		bool RunMethod_T(const Address& aContract, const Address& aCaller, const TMethod& m)
		{
			return RunMethod(aContract, aCaller, m, sizeof(m));
		}

	};


	void EvmTest1()
	{
		// storage example

/*
		pragma solidity >=0.7.0 <0.9.0;

		contract Storage {

			uint256 m_Num;

			function add(uint num) public returns (uint256) {
				uint256 val = m_Num;
				val += num;
				m_Num = val;
				return val;
				//m_Num += num;
				//return m_Num;
			}
		}
*/

		static const char szCode[] = "";

		// owner example
		//static const char szCode[] = "";

		MyProcessor evm;

		MyProcessor::Address aContract, aOwner;
		ECC::GenRandom(aContract);
		ECC::GenRandom(aOwner);

		verify_test(evm.Construct(aContract, aOwner, szCode, _countof(szCode) - 1, nullptr, 0));

#pragma pack (push, 1)
		struct MyMethod
			:public EvmProcessor::Method
		{
			EvmProcessor::Word m_MyValue;
		} myArg;
#pragma pack (pop)

		uint64_t valExpected = 0;
		for (uint32_t i = 0; i < 10; i++)
		{
			uint32_t valAdd = (i + 5) * 19;
			valExpected += valAdd;

			myArg.SetSelector("add(uint256)");
			myArg.m_MyValue = valAdd;

			verify_test(evm.RunMethod_T(aContract, aOwner, myArg));

			verify_test(sizeof(EvmProcessor::Word) == evm.m_RetVal.m_Blob.n);
			const auto& wRes = *(const EvmProcessor::Word*) evm.m_RetVal.m_Blob.p;

			verify_test(EvmProcessor::Word(valExpected) == wRes);

		}
	}

	void EvmTest2()
	{
		// demo voting contract
		static const char szCode[] = "";

		MyProcessor evm;

		MyProcessor::Address aContract, aOwner;
		ECC::GenRandom(aContract);
		ECC::GenRandom(aOwner);

		{
#pragma pack (push, 1)
			struct MyArgs
			{
				EvmProcessor::Word m_ArrMark;
				EvmProcessor::Word m_Count;
				EvmProcessor::Word m_pszName[2];
			} myArgs;
#pragma pack (pop)

			myArgs.m_ArrMark = 0x20u;
			myArgs.m_Count = _countof(myArgs.m_pszName);
			myArgs.m_pszName[0] = Zero;
			myArgs.m_pszName[1] = Zero;
			memcpy(myArgs.m_pszName[0].m_pData, "Hello", 5);
			memcpy(myArgs.m_pszName[1].m_pData, "world!", 6);

			verify_test(evm.Construct(aContract, aOwner, szCode, _countof(szCode) - 1, &myArgs, sizeof(myArgs)));
		}


		MyProcessor::Address aVoter;
		ECC::GenRandom(aVoter);

		{
#pragma pack (push, 1)
			struct MyMethod
				:public EvmProcessor::Method
			{
				EvmProcessor::Word m_wVoter;
			} myArg;
#pragma pack (pop)

			myArg.SetSelector("giveRightToVote(address)");
			myArg.m_wVoter = aVoter.ToWord();

			verify_test(evm.RunMethod_T(aContract, aOwner, myArg));
			verify_test(!evm.m_RetVal.m_Blob.n);
		}

		{
#pragma pack (push, 1)
			struct MyMethod
				:public EvmProcessor::Method
			{
				EvmProcessor::Word m_iVote;
			} myArg;
#pragma pack (pop)

			myArg.SetSelector("vote(uint256)");
			myArg.m_iVote = 1u;

			verify_test(evm.RunMethod_T(aContract, aVoter, myArg));
			verify_test(!evm.m_RetVal.m_Blob.n);
		}

		{
			EvmProcessor::Method myArg;
			myArg.SetSelector("winningProposal()");

			verify_test(evm.RunMethod_T(aContract, aVoter, myArg));

			verify_test(sizeof(EvmProcessor::Word) == evm.m_RetVal.m_Blob.n);
			const auto& wRes = *(const EvmProcessor::Word*)evm.m_RetVal.m_Blob.p;
			verify_test(EvmProcessor::Word(1u) == wRes);
		}

		{
			EvmProcessor::Method myArg;
			myArg.SetSelector("winnerName()");

			verify_test(evm.RunMethod_T(aContract, aVoter, myArg));
			verify_test(sizeof(EvmProcessor::Word) == evm.m_RetVal.m_Blob.n);
		}

	}

	void EvmTest3()
	{
		// uniswapv2
		static const char szCodeUniswapV2Pair_1[] = "";
		static const char szCodeUniswapV2Pair_2[] = "";
		static const char szCodeUniswapV2Pair_3[] = "";

		static const char szCodeUniswapV2ERC20[] = "";

		static const char szCodeUniswapV2Factory_1[] = "";
		static const char szCodeUniswapV2Factory_2[] = "";
		static const char szCodeUniswapV2Factory_3[] = "";
		static const char szCodeUniswapV2Factory_4[] = "";

		MyProcessor evm;

		MyProcessor::Address aContract, aOwner, aBenefit;
		ECC::GenRandom(aContract);
		ECC::GenRandom(aOwner);
		ECC::GenRandom(aBenefit);

		{


			std::string sCode = szCodeUniswapV2Factory_1;
			sCode += szCodeUniswapV2Factory_2;
			sCode += szCodeUniswapV2Factory_3;
			sCode += szCodeUniswapV2Factory_4;

			auto wBenefit = aBenefit.ToWord();
			verify_test(evm.Construct(aContract, aOwner, sCode.c_str(), (uint32_t) sCode.size(), wBenefit.m_pData, wBenefit.nBytes));
		}

		MyProcessor::Address aTokA, aTokB;
		ECC::GenRandom(aTokA);
		ECC::GenRandom(aTokB);

		{
			// "c9c65396": "createPair(address,address)",
#pragma pack (push, 1)
			struct MyMethod
				:public EvmProcessor::Method
			{
				EvmProcessor::Word m_wTokA;
				EvmProcessor::Word m_wTokB;
			} myArg;
#pragma pack (pop)

			myArg.SetSelector("createPair(address,address)");
			myArg.m_wTokA = aTokA.ToWord();
			myArg.m_wTokB = aTokB.ToWord();

			verify_test(evm.RunMethod_T(aContract, aOwner, myArg));
			verify_test(!evm.m_RetVal.m_Blob.n);

		}

	}

} // namespace beam

int main()
{
	try
	{
		ECC::PseudoRandomGenerator prg;
		ECC::PseudoRandomGenerator::Scope scope(&prg);

		using namespace beam;

		beam::EvmTest1();
		beam::EvmTest2();
		//beam::EvmTest3();

	}
	catch (const std::exception & ex)
	{
		printf("Expression: %s\n", ex.what());
		g_TestsFailed++;
	}

	return g_TestsFailed ? -1 : 0;
}
